import Vue from 'vue'
import VueRouter from 'vue-router'
import Vuex from 'vuex'
import router from '../router'
import axios from 'axios'
import VueAxios from 'vue-axios'

Vue.use(Vuex)
Vue.use(VueAxios, axios)

export default new Vuex.Store({
    state: {
        usuario : null,
        rol : null
    },
    mutations: {
        setUsuario(state,usuario,rol){
            state.usuario = usuario,
            state.rol = rol
        }
    },
    actions: {
        authUser ({commit},usuario ){
            let postdata = {
                user: usuario.username,
                pwd:  usuario.password,
            };
            axios.get("http://192.168.1.238:92/api/login.php",{
                params:postdata
            }).then((result) => {
                if(result.data == "Error"){
                    alert("Error usuario o contraseña erroneo")
                    location.reload();
                }
                else{
                    let data = result.data.split("||");
                    let Menu = data[0];
                    let UserJson = JSON.parse(data[1]);
                    let usuarioIngreso = {
                        usuario : UserJson.UserName,
                        rol : UserJson.Perfil,
                        emplid : UserJson.Emplid,
                        nombre: UserJson.Nombre,
                        img: UserJson.Image,
                        puesto: UserJson.Puesto,
                        departamento : UserJson.Depto,
                        area : UserJson.Area,
                        areaid: UserJson.Areaid,
                        deptid: UserJson.Deptid,
                        mail: UserJson.Correo,
                        Usermenu: Menu,
                        ingreso: UserJson.Ingreso.date,
                        antiguedad: UserJson.Antiguedad,
                        responsablenum: UserJson.Responsablenum,
                        responsablenom: UserJson.Responsablenom,
                        imss: UserJson.Imss,
                        rfc: UserJson.Rfc,
                        curp: UserJson.Curp,
                        real: 1,
                        user : usuario.username,
                        pass : usuario.password
                    };
                    localStorage.setItem('USER_AUTH',JSON.stringify(usuarioIngreso));
                    localStorage.setItem('MENU',Menu);
                    commit('setUsuario',usuarioIngreso);
                    router.push("/");
                }            
            }).catch(e => {
                console.log(e)
            });
        },
        checkAuth({commit}){
            let userauth = JSON.parse(localStorage.getItem("USER_AUTH"));
            if(userauth == null){
                router.push("/login");
                commit('setUsuario',null);
            }else{
                commit('setUsuario',userauth);
            }
        },
        logOut ({commit}){
            commit('setUsuario',null);
            storage.clear();
            router.push("/login",userauth);
        },        
        loginAs ({commit},usuario ){
            let postdata = {
                emplid: usuario.emplid,
                user: usuario.user,
                pass: usuario.pass
            };
            axios.get("http://192.168.1.238:92/api/loginas.php",{
                params:postdata
            }).then((result) => {
                if(result.data == "Error"){
                    alert("Error usuario o contraseña erroneo")
                }
                else{
                    console.log(result.data);
                    let data = result.data.split("||");
                    let Menu = data[0];
                    let UserJson = JSON.parse(data[1]);
                    let usuarioIngreso = {
                        usuario : UserJson.UserName,
                        rol : UserJson.Perfil,
                        emplid : UserJson.Emplid,
                        nombre: UserJson.Nombre,
                        img: UserJson.Image,
                        puesto: UserJson.Puesto,
                        departamento : UserJson.Depto,
                        area : UserJson.Area,
                        areaid: UserJson.Areaid,
                        deptid: UserJson.Deptid,
                        mail: UserJson.Correo,
                        Usermenu: Menu,
                        ingreso: UserJson.Ingreso.date,
                        antiguedad: UserJson.Antiguedad,
                        responsablenum: UserJson.Responsablenum,
                        responsablenom: UserJson.Responsablenom,
                        imss: UserJson.Imss,
                        rfc: UserJson.Rfc,
                        curp: UserJson.Curp,
                        real: 0,
                        user : postdata.user,
                        pass : postdata.pass
                    };
                    localStorage.setItem('USER_AUTH',JSON.stringify(usuarioIngreso));
                    localStorage.setItem('MENU',Menu);
                    commit('setUsuario',usuarioIngreso);
                    window.location.reload();
                }            
            }).catch(e => {
                console.log(e)
            });
        },
        Backtomy ({commit},usuario ){
            let postdata = {
                user: usuario.username,
                pwd:  usuario.password,
            };
            axios.get("http://192.168.1.238:92/api/login.php",{
                params:postdata
            }).then((result) => {
                if(result.data == "Error"){
                    alert("Error usuario o contraseña erroneo")
                    location.reload();
                }
                else{
                    let data = result.data.split("||");
                    let Menu = data[0];
                    let UserJson = JSON.parse(data[1]);
                    let usuarioIngreso = {
                        usuario : UserJson.UserName,
                        rol : UserJson.Perfil,
                        emplid : UserJson.Emplid,
                        nombre: UserJson.Nombre,
                        img: UserJson.Image,
                        puesto: UserJson.Puesto,
                        departamento : UserJson.Depto,
                        area : UserJson.Area,
                        areaid: UserJson.Areaid,
                        deptid: UserJson.Deptid,
                        mail: UserJson.Correo,
                        Usermenu: Menu,
                        ingreso: UserJson.Ingreso.date,
                        antiguedad: UserJson.Antiguedad,
                        responsablenum: UserJson.Responsablenum,
                        responsablenom: UserJson.Responsablenom,
                        imss: UserJson.Imss,
                        rfc: UserJson.Rfc,
                        curp: UserJson.Curp,
                        real: 1,
                        user : usuario.username,
                        pass : usuario.password
                    };
                    localStorage.setItem('USER_AUTH',JSON.stringify(usuarioIngreso));
                    localStorage.setItem('MENU',Menu);
                    commit('setUsuario',usuarioIngreso);
                    window.location.reload();
                }            
            }).catch(e => {
                console.log(e)
            });
        },
    },
    modules: {
    }
})
